CREATE TABLE api_country (
  id SERIAL PRIMARY KEY,
  name VARCHAR NOT NULL UNIQUE,
  iso_a3 VARCHAR NOT NULL UNIQUE,
  lat FLOAT NOT NULL,
  lon FLOAT NOT NULL,
  population INT NOT NULL,
  area INT NOT NULL
);

GRANT ALL PRIVILEGES ON TABLE api_country TO clayton;
GRANT ALL PRIVILEGES ON TABLE api_country_id_seq TO clayton;

